INSERT INTO `classes` (`Class_Id`, `Class_Name`, `Description`, `Status`, `Image_URL`) VALUES
(1, 'Python 101', 'Introduction to Python - first course in programming. Course is meant for absolute beginners!\r\n\r\nStudents can take \"AP CS Principles\" in school and get certified as PCEP -Certified Entry-Level Python Programmer.\r\n\r\nNext batch runs from Sep 2026 to May 2027 on weekends.', 'Approved', 'images/banner_images/classes/python_101.png'),
(3, 'Java 101', '(Not offered now) Introduction to Java and Object Oriented Programming.\r\n\r\nMaps to \"Computer Science AP CS A\".\r\n\r\nhttps://apstudents.collegeboard.org/courses/ap-computer-science-a', 'Inactive', 'images/banner_images/classes/java101.jpg'),
(4, 'Snap 101', 'Interesting in learning programming through gaming?\r\n\r\nSnap! (developed by University of California, Berkeley) is a free block-based educational graphical programming language and online community.', 'Inactive', 'images/banner_images/classes/snap101.png'),
(5, 'Python DS', 'Python for Data Science.\r\n\r\nPrerequisite: Python 101\r\n\r\nThis advanced course is designed for students who have completed Python 101 and are ready to dive deeper. The curriculum covers Python Object-Oriented Programming (OOP), NumPy and Pandas, and  visualization tools such as Matplotlib and others. Students will apply their learning in a capstone solo project, gaining hands-on experience in solving real-world data science problems.\r\n\r\nNext batch runs from Sep 2026 to May 2027 on weekends.', 'Approved', 'images/banner_images/classes/python_ds.png'),
(6, 'Python ML', 'Python for Machine Learning.\r\n\r\nPrerequisite: Python for Data Science\r\n\r\nThis course introduces students to core machine learning concepts and algorithms. Students will learn to implement and evaluate the Top 10 Machine Learning algorithms in the areas of classification, regression, and clustering. The course uses NumPy, Pandas, Matplotlib, and scikit-learn, with an introduction to frameworks such as TensorFlow and Keras. The course concludes with a solo Machine Learning project, where students apply the concepts learned to a real-world dataset.\r\n\r\nOffered on Saturdays from 9 AM to 10 AM CST (9/6/25 to 5/23/26)\r\n\r\n\r\n', 'Inactive', 'images/banner_images/classes/python_ml.png'),
(7, 'SQL 101', 'SQL for beginners.\r\n\r\nIn this course, we will learn about SQL.\r\n\r\nWe will explore several tools to explore the database concepts.\r\n\r\nWe will also build a Python or Java application which extracts the data from MySQL data.', 'Inactive', 'images/banner_images/classes/sql101.png'),
(8, 'HTML/CSS 101', 'HTML (content) and CSS (display) are the core technologies of the Web. Coupled with JavaScript (for behavior), these technologies run 99% of the WebSites. In this course, we will learn about HTML and CSS. Students will build a website to showcase their profile.', 'Inactive', 'images/banner_images/classes/css101.png'),
(9, 'JavaScript 101', 'JavaScript, often abbreviated as JS, is a programming language and core technology of the Web, alongside HTML and CSS. 99% of websites use JavaScript on the client side for webpage behavior.\r\n\r\nIn this course, we will learn about JavaScript.\r\n\r\nMaps to JSE ‚Äì Certified Entry-Level JavaScript Programmer (Exam JSE-40-0x)\r\n\r\nhttps://edube.org/study/jse1', 'Inactive', 'images/banner_images/classes/javascript101.png'),
(10, 'PHP 101', 'PHP is a server scripting language, and a powerful tool for making dynamic and interactive Web pages.\r\n\r\nIn this course, we will learn about PHP.\r\n\r\nStudents will build an interactive web-site involving HTML, CSS, JavaScript, PHP and MySQL databases.', 'Inactive', 'images/banner_images/classes/php101.jpg'),
(12, 'Python AI', 'Python for Artificial Intelligence\r\n\r\n(Will be offered next year) \r\n\r\nPrerequisite: Python for Machine Learning\r\n\r\nThis course introduces students to modern Artificial Intelligence concepts and applications. Topics include Generative AI, Large Language Models (LLMs), building agents with LLMs, and Agentic AI involving workflows. Students will gain hands-on experience with several AI and LLM tools. The course concludes with a solo project, where each student designs and builds an AI agent‚Äîeither for personal productivity or to address a practical problem.', 'Approved', 'images/banner_images/classes/python_ai.png');
